import React from 'react'
import Header from './Header'
import Note from './Note'
import Footer from './Footer'
import Notes from '../Notes'
import CreateArea from './CreateArea'

function App() {

  return (
    <div>
        <Header/>
        <CreateArea 
        onAdd={addNote}
        />
        {Notes.map(data=>
        <Note 
          key={data.key}
          title={data.title}
          content={data.content}
          />
        )}
  
        <Footer/>
    </div>
  )
}

export default App;
