    <div>
      <form>
        <input 
        name="title" 
        placeholder="Title" 
        onChange={handleChange}
        value={Note.title}
        />
        <textarea 
        name="content" 
        value={Note.content}
        onChange={handleChange}
        placeholder="Take a note..." 
        rows="3" />
        <button>Add</button>
      </form>
    </div>
  );
}

export default CreateArea;
