import React from 'react'

function CreateArea() {

    const [note,setNote]=useState({
        title:"",
        content:""
    });

    function handleChange(event)
    {
        const{name,value}=event.target;
        setNote(prevNote => {
            return {
                ...prevNote,
                [name] :value
            };
        });
    }

  return (
    <div>
      <form>
        <input 
        name="title" 
        value={note.title}
        onChange={handleChange}
        placeholder="title" 
        />
        <textarea 
        name="content" 
        onChange={handleChange}
        value={note.content}  
        placeholder="Take a note..."
        rows="3">
      </textarea>
        <button>Add</button>
      </form>
    </div>
  )
}

export default CreateArea
